// Générer les métadonnées pour SEO
export async function generateMetadata({ params }: BlogPageProps) {
  const { locale, slug } = await params;
  const article = await fetchArticleBySlug(slug, locale);

  if (!article) {
    return {
      title: locale === 'en' ? 'Article not found' : 'Article non trouvé',
    };
  }

  return {
    title: article.title,
    description: article.description,
    openGraph: {
      title: article.title,
      description: article.description,
      images: article.image ? [article.image.url] : [],
    },
  };
}

import fetchContentType from "@/lib/strapi/fetchContentType";
import ClientSlugHandler from '../ClientSlugHandler';
import Link from 'next/link';
import Image from 'next/image';
import { Badge } from '@/components/ui/badge';
import { Article } from '@/types/types';

interface BlogIndexProps {
  params: {
    locale: string;
  };
}

export default async function BlogIndex({ params }: BlogIndexProps) {
  const { locale } = await params;

  const articlesResponse = await fetchContentType('articles', {
    populate: {
      image: {
        populate: '*'
      },
      categories: {
        populate: '*'
      }
    },
    locale: locale,
    sort: ['publishedAt:desc']
  });

  const articles: Article[] = articlesResponse?.data || [];

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('fr-FR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <main className="container mx-auto py-6 max-w-6xl">
      <ClientSlugHandler localizedSlugs={{ [locale]: 'blog' }} />

      <div className="mb-12">
        <h1 className="text-4xl md:text-5xl font-bold mb-4 text-gray-900 dark:text-white">
          Blog
        </h1>
        <p className="text-xl text-gray-600 dark:text-gray-300">
          Découvrez nos derniers articles et actualités
        </p>
      </div>

      {articles.length === 0 ? (
        <div className="text-center py-12">
          <p className="text-gray-500 dark:text-gray-400 text-lg">
            Aucun article disponible pour le moment.
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {articles.map((article) => (
            <article key={article.slug} className="group">
              <Link href={`/${locale}/blog/${article.slug}`} className="block">
                <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md overflow-hidden transition-transform duration-200 group-hover:scale-105">
                  {/* Image de l'article */}
                  {article.image && (
                    <div className="relative w-full h-48">
                      <Image
                        src={`${process.env.NEXT_PUBLIC_API_URL}${article.image.url}`}
                        alt={article.image.alternativeText || article.title}
                        fill
                        className="object-cover"
                      />
                    </div>
                  )}

                  <div className="p-6">
                    {/* Catégories */}
                    {article.categories && article.categories.length > 0 && (
                      <div className="flex flex-wrap gap-2 mb-3">
                        {article.categories.slice(0, 2).map((category, index) => (
                          <Badge key={index} variant="secondary" className="text-xs">
                            {category.name}
                          </Badge>
                        ))}
                      </div>
                    )}

                    {/* Titre */}
                    <h2 className="text-xl font-bold mb-3 text-gray-900 dark:text-white group-hover:text-blue-600 dark:group-hover:text-blue-400 transition-colors">
                      {article.title}
                    </h2>

                    {/* Description */}
                    <p className="text-gray-600 dark:text-gray-300 mb-4 line-clamp-3">
                      {article.description}
                    </p>

                    {/* Date de publication */}
                    <time
                      dateTime={article.publishedAt}
                      className="text-sm text-gray-500 dark:text-gray-400"
                    >
                      {formatDate(article.publishedAt)}
                    </time>
                  </div>
                </div>
              </Link>
            </article>
          ))}
        </div>
      )}
    </main>
  );
}

