import Link from 'next/link';
import { Button } from '@/components/ui/button';
import { ChevronLeft, ChevronRight } from 'lucide-react';

interface ArticleNavigationProps {
  previousArticle?: {
    title: string;
    slug: string;
  };
  nextArticle?: {
    title: string;
    slug: string;
  };
  locale: string;
}

export default function ArticleNavigation({
  previousArticle,
  nextArticle,
  locale
}: ArticleNavigationProps) {
  const getTexts = (locale: string) => {
    return locale === 'en'
      ? {
          previous: 'Previous article',
          next: 'Next article',
          backToNews: 'Back to News'
        }
      : {
          previous: 'Article précédent',
          next: 'Article suivant',
          backToNews: 'Retour aux actualités'
        };
  };

  const texts = getTexts(locale);

  return (
    <div className="mt-12 pt-8 border-t border-gray-200 dark:border-gray-700">
      {/* Bouton retour vers la liste */}
      <div className="mb-8">
        <Link href={`/${locale}/news`}>
          <Button variant="outline" className="mb-4">
            <ChevronLeft className="w-4 h-4 mr-2" />
            {texts.backToNews}
          </Button>
        </Link>
      </div>

      {/* Navigation entre articles */}
      {(previousArticle || nextArticle) && (
        <nav className="flex justify-between items-center">
          <div className="flex-1">
            {previousArticle && (
              <Link
                href={`/${locale}/news/${previousArticle.slug}`}
                className="group block"
              >
                <div className="flex items-center text-sm text-gray-500 dark:text-gray-400 mb-1">
                  <ChevronLeft className="w-4 h-4 mr-1" />
                  {texts.previous}
                </div>
                <div className="text-lg font-medium text-gray-900 dark:text-white group-hover:text-blue-600 dark:group-hover:text-blue-400 transition-colors">
                  {previousArticle.title}
                </div>
              </Link>
            )}
          </div>

          <div className="flex-1 text-right">
            {nextArticle && (
              <Link
                href={`/${locale}/news/${nextArticle.slug}`}
                className="group block"
              >
                <div className="flex items-center justify-end text-sm text-gray-500 dark:text-gray-400 mb-1">
                  {texts.next}
                  <ChevronRight className="w-4 h-4 ml-1" />
                </div>
                <div className="text-lg font-medium text-gray-900 dark:text-white group-hover:text-blue-600 dark:group-hover:text-blue-400 transition-colors">
                  {nextArticle.title}
                </div>
              </Link>
            )}
          </div>
        </nav>
      )}
    </div>
  );
}

import { notFound } from 'next/navigation';
import { fetchArticleBySlug } from '@/lib/strapi/fetchArticleBySlug';
import ClientSlugHandler from '../../ClientSlugHandler';
import ArticleContent from './components/ArticleContent';
import ArticleMeta from './components/ArticleMeta';
import Hero from '@/components/Hero';
import { Article } from '@/types/types';

interface NewsPageProps {
  params: {
    locale: string;
    slug: string;
  };
}

export default async function NewsPage({ params }: NewsPageProps) {
  const { locale, slug } = await params;

  const article: Article | null = await fetchArticleBySlug(slug, locale);

  if (!article) {
    notFound();
  }

  // Créer les slugs localisés pour le ClientSlugHandler
  const localizedSlugs = article.localizations?.reduce(
    (acc: Record<string, string>, localization: any) => {
      acc[localization.locale] = localization.slug;
      return acc;
    },
    { [locale]: slug }
  ) || { [locale]: slug };

  return (
    <main className="min-h-screen">
      <ClientSlugHandler localizedSlugs={localizedSlugs} />

      {/* Hero Section */}
      <Hero
        title={article.title}
        description={article.description}
        backgroundImage={article.image ? `${process.env.NEXT_PUBLIC_API_URL}${article.image.url}` : undefined}
        size="lg"
        overlay={true}
      />

      {/* Article Content */}
      <section className="container mx-auto py-12 max-w-4xl px-4">
        <article className="prose prose-lg max-w-none dark:prose-invert">
          <ArticleMeta article={article} locale={locale} />
          <ArticleContent article={article} />
        </article>
      </section>
    </main>
  );
}

// Générer les métadonnées pour SEO
export async function generateMetadata({ params }: NewsPageProps) {
  const { locale, slug } = await params;
  const article = await fetchArticleBySlug(slug, locale);

  if (!article) {
    return {
      title: locale === 'en' ? 'Article not found' : 'Article non trouvé',
    };
  }

  return {
    title: article.title,
    description: article.description,
    openGraph: {
      title: article.title,
      description: article.description,
      images: article.image ? [article.image.url] : [],
    },
  };
}

