import { cn } from '@/lib/utils';

interface CKEditorContentProps {
  content: string;
  className?: string;
}

export default function CKEditorContent({ content, className }: CKEditorContentProps) {
  return (
    <div
      className={cn(
        // Base prose styles
        "prose prose-lg max-w-none dark:prose-invert",
        // Enhanced CKEditor specific styles
        "ck-content",
        // Lists styling
        "[&_ul]:list-disc [&_ul]:ml-6 [&_ul]:my-4",
        "[&_ol]:list-decimal [&_ol]:ml-6 [&_ol]:my-4",
        "[&_li]:my-2 [&_li]:leading-relaxed",
        // Nested lists
        "[&_ul_ul]:list-[circle] [&_ul_ul]:ml-4",
        "[&_ol_ol]:list-[lower-alpha] [&_ol_ol]:ml-4",
        // Images styling
        "[&_img]:max-w-full [&_img]:h-auto [&_img]:my-6",
        "[&_figure]:my-6 [&_figure]:mx-0",
        "[&_figure_img]:mb-2",
        "[&_figcaption]:text-sm [&_figcaption]:text-gray-600 [&_figcaption]:italic [&_figcaption]:text-center",
        // Image alignment classes that CKEditor generates
        "[&_.image-style-align-left]:float-left [&_.image-style-align-left]:mr-4 [&_.image-style-align-left]:mb-4",
        "[&_.image-style-align-right]:float-right [&_.image-style-align-right]:ml-4 [&_.image-style-align-right]:mb-4",
        "[&_.image-style-align-center]:mx-auto [&_.image-style-align-center]:block",
        "[&_.image-style-side]:float-right [&_.image-style-side]:ml-4 [&_.image-style-side]:mb-4 [&_.image-style-side]:max-w-[50%]",
        // Blockquotes
        "[&_blockquote]:border-l-4 [&_blockquote]:border-blue-500 [&_blockquote]:pl-4 [&_blockquote]:italic [&_blockquote]:my-6",
        // Code blocks
