    lg: 'text-4xl md:text-5xl lg:text-6xl'
  };

  return (
    <section className={`bg-gray-50 dark:bg-gray-900 ${sizeClasses[size]} ${className}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center">
