'use client';

import Image from 'next/image';
import Link from 'next/link';
import { HomePage } from '@/types/types';
import { strapiImage } from '@/lib/strapi/strapiImage';

interface HomeHeroProps {
  homePage: HomePage;
  locale: string;
}

export default function HomeHero({ homePage, locale }: HomeHeroProps) {
  if (!homePage) {
    return null;
  }

  const { name, description, background, firstButton, secondButton } = homePage;

  // Get the background image (use the first one if multiple exist)
  const backgroundImage = background && background.length > 0 ? background[0] : null;
  const backgroundUrl = backgroundImage?.formats?.large?.url ||
                       backgroundImage?.formats?.medium?.url ||
                       backgroundImage?.url;

  // Helper function to get target attribute similar to navbar
  const getTargetAttribute = (target?: string | null) => {
    if (!target || target === 'null' || target === 'self') return undefined;
    return target;
  };

  return (
    <section className="relative min-h-screen flex items-center justify-center overflow-hidden">
      {/* Background Image */}
      {backgroundUrl && (
        <div className="absolute inset-0 z-0">
          <Image
            src={strapiImage(backgroundUrl)}
            alt={backgroundImage?.alternativeText || name}
            fill
            className="object-cover"
            priority
            sizes="100vw"
          />
          {/* Dark overlay for better text readability */}
          <div className="absolute inset-0 bg-black/40" />
        </div>
      )}

      {/* Content */}
      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <div className="max-w-4xl mx-auto">
          {/* Title */}
          <h1 className="text-4xl md:text-6xl lg:text-7xl font-bold text-white mb-6 leading-tight">
            {name}
          </h1>

          {/* Description */}
          <p className="text-xl md:text-2xl text-gray-200 mb-8 leading-relaxed max-w-3xl mx-auto">
            {description}
          </p>

          {/* Dynamic CTA Buttons */}
          {(firstButton || secondButton) && (
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              {firstButton && (
                <Link
                  href={`/${locale}${firstButton.URL}`}
                  target={getTargetAttribute(firstButton.target)}
                  className="px-8 py-4 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg transition-colors duration-200 shadow-lg hover:shadow-xl flex items-center justify-center text-center"
                >
                  {firstButton.text}
                </Link>
              )}
              {secondButton && (
                <Link
                  href={`/${locale}${secondButton.URL}`}
                  target={getTargetAttribute(secondButton.target)}
                  className="px-8 py-4 bg-transparent hover:bg-white/10 text-white font-semibold rounded-lg border-2 border-white transition-colors duration-200 flex items-center justify-center text-center"
                >
                  {secondButton.text}
                </Link>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Scroll indicator */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-10">
        <div className="animate-bounce">
          <svg
            className="w-6 h-6 text-white"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M19 14l-7 7m0 0l-7-7m7 7V3"
            />
          </svg>
        </div>
      </div>
    </section>
  );
}

import React from 'react';

interface NewsHeroProps {
  title: string;
  description?: string;
  backgroundImage?: string;
  size?: 'sm' | 'md' | 'lg';
  overlay?: boolean;
  className?: string;
}

const NewsHero: React.FC<NewsHeroProps> = ({ 
  title, 
  description, 
  backgroundImage,
  size = 'lg', 
  overlay = true,
  className = '' 
}) => {
  const sizeClasses = {
    sm: 'py-20 md:py-24',
    md: 'py-24 md:py-32',
    lg: 'py-32 md:py-40 lg:py-48'
  };

  const titleSizeClasses = {
    sm: 'text-2xl md:text-3xl',
    md: 'text-3xl md:text-4xl lg:text-5xl',
    lg: 'text-4xl md:text-5xl lg:text-6xl'
  };

  const backgroundStyle = backgroundImage ? {
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat'
  } : {};

  return (
    <section 
      className={`relative ${sizeClasses[size]} ${className}`}
      style={backgroundStyle}
    >
      {/* Overlay pour améliorer la lisibilité du texte */}
      {overlay && (
        <div className="absolute inset-0 bg-black/50 dark:bg-black/60"></div>
      )}

      {/* Fallback background si pas d'image */}
      {!backgroundImage && (
        <div className="absolute inset-0 bg-gray-50 dark:bg-gray-900"></div>
      )}

      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center">
          <h1 className={`${titleSizeClasses[size]} font-bold text-white mb-4 drop-shadow-lg`}>
            {title}
          </h1>
          {description && (
            <p className="text-lg md:text-xl text-white/90 max-w-3xl mx-auto drop-shadow-md">
              {description}
            </p>
          )}
        </div>
      </div>
    </section>
  );
}

export default NewsHero;

import Link from 'next/link';
import {
  Pagination,
  PaginationContent,
  PaginationEllipsis,
  PaginationItem,
  PaginationLink,
  PaginationNext,
  PaginationPrevious,
} from "@/components/ui/pagination";

interface NewsPaginationProps {
  currentPage: number;
  totalPages: number;
  locale: string;
  baseUrl: string;
}

export default function NewsPagination({ 
  currentPage, 
  totalPages, 
  locale,
  baseUrl 
}: NewsPaginationProps) {
  const getTexts = (locale: string) => {
    return locale === 'en'
      ? {
          previous: 'Previous',
          next: 'Next'
        }
      : {
          previous: 'Précédent',
          next: 'Suivant'
        };
  };

  const texts = getTexts(locale);

  // Si il n'y a qu'une page ou moins, ne pas afficher la pagination
  if (totalPages <= 1) return null;

  // Générer les numéros de pages à afficher
  const getPageNumbers = () => {
    const pages = [];
    const maxVisiblePages = 5;

    if (totalPages <= maxVisiblePages) {
      // Afficher toutes les pages si moins de 5
      for (let i = 1; i <= totalPages; i++) {
        pages.push(i);
      }
    } else {
      // Logique pour afficher les pages avec ellipses
      if (currentPage <= 3) {
        // Au début
        pages.push(1, 2, 3, 4, '...', totalPages);
      } else if (currentPage >= totalPages - 2) {
        // À la fin
        pages.push(1, '...', totalPages - 3, totalPages - 2, totalPages - 1, totalPages);
      } else {
        // Au milieu
        pages.push(1, '...', currentPage - 1, currentPage, currentPage + 1, '...', totalPages);
      }
    }

    return pages;
  };

  const pageNumbers = getPageNumbers();

  return (
    <div className="mt-12">
      <Pagination>
        <PaginationContent>
          {/* Bouton Précédent */}
          <PaginationItem>
            {currentPage > 1 ? (
              <Link href={`${baseUrl}?page=${currentPage - 1}`}>
                <PaginationPrevious>
                  <span>{texts.previous}</span>
                </PaginationPrevious>
              </Link>
            ) : (
              <PaginationPrevious className="opacity-50 cursor-not-allowed pointer-events-none">
                <span>{texts.previous}</span>
              </PaginationPrevious>
            )}
          </PaginationItem>

          {/* Numéros de pages */}
          {pageNumbers.map((page, index) => (
            <PaginationItem key={index}>
              {page === '...' ? (
                <PaginationEllipsis />
              ) : (
                <Link href={`${baseUrl}?page=${page}`}>
                  <PaginationLink
                    isActive={page === currentPage}
                    className={page === currentPage ? "bg-primary text-primary-foreground" : ""}
                  >
                    {page}
                  </PaginationLink>
                </Link>
              )}
            </PaginationItem>
          ))}

          {/* Bouton Suivant */}
          <PaginationItem>
            {currentPage < totalPages ? (
              <Link href={`${baseUrl}?page=${currentPage + 1}`}>
                <PaginationNext>
                  <span>{texts.next}</span>
                </PaginationNext>
              </Link>
            ) : (
              <PaginationNext className="opacity-50 cursor-not-allowed pointer-events-none">
                <span>{texts.next}</span>
              </PaginationNext>
            )}
          </PaginationItem>
        </PaginationContent>
      </Pagination>
    </div>
  );
}

import Image from 'next/image';
import Link from 'next/link';
import { Badge } from '@/components/ui/badge';
import { MemberProfile } from '@/types/types';
import { strapiImage } from '@/lib/strapi/strapiImage';
import PublicationLine from '@/components/publications/PublicationLine';
export default function MemberDetail({ member, locale }: { member: MemberProfile; locale: string }) {
  const fullName = `${member.firstName} ${member.lastName}`.trim();
  const imgUrl = member.image?.formats?.medium?.url
    || member.image?.formats?.small?.url
    || member.image?.formats?.thumbnail?.url
    || member.image?.url;

  const t = (k: string) => {
    const dict: Record<string, Record<string, string>> = {
      en: {
        role: 'Roles',
        contact: 'Contact',
        email: 'Email',
        phone: 'Phone',
        bio: 'Biography',
        publications: 'Publications',
        noPublications: 'No publications yet.'
      },
      fr: {
        role: 'Rôles',
        contact: 'Contact',
        email: 'Email',
        phone: 'Téléphone',
        bio: 'Biographie',
        publications: 'Publications',
        noPublications: 'Aucune publication pour le moment.'
      },
    };
    return (dict[locale] || dict.fr)[k] || k;
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
      {/* Sidebar */}
      <aside className="lg:col-span-1 space-y-6">
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <div className="relative w-40 h-40 mx-auto rounded-full overflow-hidden bg-gray-100 dark:bg-gray-900">
            {imgUrl && (
              <Image src={strapiImage(imgUrl)} alt={member.image?.alternativeText || fullName} fill className="object-cover" />
            )}
          </div>
          <h2 className="mt-4 text-center text-xl font-semibold text-gray-900 dark:text-white">{fullName}</h2>
          {member.members_roles?.length ? (
            <div className="mt-4 flex flex-wrap justify-center gap-2">
              {member.members_roles.map((r, i) => (
                <Badge key={i} variant="secondary" className="text-xs">{r.name}</Badge>
              ))}
            </div>
          ) : null}
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <h3 className="text-sm font-medium text-gray-500 dark:text-gray-400 mb-3">{t('contact')}</h3>
          <ul className="space-y-2 text-sm">
            {member.email && (
              <li>
                <span className="font-medium">{t('email')}:</span>{' '}
                <Link href={`mailto:${member.email}`} className="text-blue-600 dark:text-blue-400 hover:underline">{member.email}</Link>
              </li>
            )}
            {member.phone_number && (
              <li>
                <span className="font-medium">{t('phone')}:</span>{' '}
                <span className="text-gray-700 dark:text-gray-200">{member.phone_number}</span>
              </li>
            )}
          </ul>
        </div>
      </aside>

      {/* Main content */}
      <div className="lg:col-span-2 space-y-8">
        {member.biography && (
          <section className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold mb-4">{t('bio')}</h3>
            {/* Note: assuming Strapi content is trusted. Consider sanitizing if uncertain. */}
            {/* Note: assuming Strapi content is trusted. Consider sanitizing if uncertain. */}
            <div className="prose max-w-none dark:prose-invert" dangerouslySetInnerHTML={{ __html: member.biography }} />
          </section>
        )}

        <section className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <h3 className="text-lg font-semibold mb-4">{t('publications')}</h3>
          {member.publications && member.publications.length > 0 ? (
            <ul className="space-y-4">
              {member.publications.map((p) => (
                <li key={p.id} className="border-b border-gray-200 dark:border-gray-700 pb-4 last:border-0 last:pb-0">
                  <PublicationLine publication={p} locale={locale} />
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-gray-500 dark:text-gray-400">{t('noPublications')}</p>
          )}
            <ul className="space-y-4">
              {member.publications.map((p) => (
                <li key={p.id} className="border-b border-gray-200 dark:border-gray-700 pb-4 last:border-0 last:pb-0">
                  <PublicationLine publication={p} locale={locale} />
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-gray-500 dark:text-gray-400">{t('noPublications')}</p>
          )}
        </section>
      </div>
    </div>
  );
}

